# One-liner version for ConnectWise ScreenConnect - Copy and paste this entire line:
powershell -Command "Write-Host 'Computer: $env:COMPUTERNAME' -ForegroundColor Yellow; Write-Host 'IP: ' -NoNewline; (Get-NetIPAddress -AddressFamily IPv4 | Where-Object {$_.IPAddress -notlike '169.254.*' -and $_.IPAddress -ne '127.0.0.1'} | Select-Object -First 1).IPAddress; $found=$false; Get-ItemProperty 'HKLM:\Software\Microsoft\Windows\CurrentVersion\Uninstall\*','HKLM:\Software\WOW6432Node\Microsoft\Windows\CurrentVersion\Uninstall\*' -ErrorAction SilentlyContinue | Where-Object {$_.DisplayName -like '*CodeMeter*'} | ForEach-Object {Write-Host \"[FOUND] $($_.DisplayName) - Version: $($_.DisplayVersion)\" -ForegroundColor Green; $found=$true}; Get-Process -ErrorAction SilentlyContinue | Where-Object {$_.ProcessName -like '*CodeMeter*' -or $_.ProcessName -like '*cm*'} | ForEach-Object {Write-Host \"[RUNNING] $($_.ProcessName) (PID: $($_.Id))\" -ForegroundColor Green; $found=$true}; Get-Service -ErrorAction SilentlyContinue | Where-Object {$_.DisplayName -like '*CodeMeter*'} | ForEach-Object {Write-Host \"[SERVICE] $($_.DisplayName) - Status: $($_.Status)\" -ForegroundColor Green; $found=$true}; if(-not $found){Write-Host '[NOT FOUND] CodeMeter Runtime not detected on this system.' -ForegroundColor Red}"
